export default function SumPage() {
  const calculateSum = () => {
    let sum = 0;
    const steps = [];
    for (let i = 1; i <= 100; i++) {
      sum += i;
      steps.push(`Step ${i}: ${i} added, current sum = ${sum}`);
    }
    return steps;
  };

  const steps = calculateSum();

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Sum from 1 to 100 with Steps</h1>
      <div className="space-y-2">
        {steps.map((step, index) => (
          <p key={index} className="text-sm">{step}</p>
        ))}
      </div>
      <p className="mt-4 font-semibold">Final Sum: {steps.length > 0 ? steps[steps.length - 1].split('= ')[1] : 0}</p>
    </div>
  );
}
